import React, { useState } from "react";
import axios from "axios";
import "./styles.css";


export default function App() {
  const [ready, setReady] = useState(false);
  const [temperature, setTemperature] = useState(null);
function handleResponse(response) {
  console.log(response.data);
  setTemperature(response.data.main.temp);
}

if (ready) {
  return (
    <div className="weather-app-wrapper">
      <div className="container-1">
        <form>
          <input
            type="search"
            placeholder="Enter your city"
            id="enterCity"
            autocomplete="off"
          ></input>
          <input
            type="submit"
            className="btn btn-primary w-20"
            value="Search"
          ></input>
        </form>
      </div>{" "}
      <br /> <h1 id="city">Corrales</h1> <p> </p> <h2>Current Conditions:</h2>{" "}
      <h4 id="description">
        <em>Clear</em>
      </h4>{" "}
      <div className="units">
        <h3>
          <img src="http://openweathermap.org/img/wn/10d@2x.png" alt="rain" id="icon" className="float-center" />
          <div id="temperature">{temperature}</div>
          <a href="#" id="celsius-link" className="active">
            °C |
          </a>
          <a href="#" id="fahrenheit-link">
            °F
          </a>
          <ul className="standard-text">
            <li>
              Humidity: <span id="humidity">30</span>%
            </li>
            <li>
              Wind: <span id="wind">25</span> km/h
            </li>
          </ul>
        </h3>
      </div>
      <br />{" "}
      <a
        className="github"
        href="https://github.com/lucy-birkhead/react-app"
      >
        React Weather App Github Repository
      </a>{" "}
      <p>Coded by Lucy B.</p>
    </div>
  );
} else {
  const apiKey = "19e635cfd49a09267430a421d97c1f42";
  let city = "Corrales";
  let apiUrl = `http://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=metric`;
  axios.get(apiUrl).then(handleResponse);

  return "Loading...";
}



}
